					var id_slide  = (me.attr('id') !== undefined) ? me.attr('id') : "rslide_" + my_responsiveslides.uniqid();
					if (me.attr('id') === undefined) {
						me.attr('id',id_slide);
					}
					me = jQuery("#" + id_slide);

					// options de base
					var settings = {
						//namespace: "rslides"
					};

					// options propres à chaque slider
					var options=eval('options='+me.attr('data-responsiveslides')+';');
					if (options) {
						//
						// gestion des thèmes
						//
						// 1. on définit un thème selon la présence du css dans les options...
						if (options.css) {
							// on ajoute le css à la liste si nécessaire
							if (stylesheets.indexOf(options.css) === -1) {
								stylesheets.push(options.css);
							}
							var theme = my_responsiveslides.css2theme(options.css);
						}
						// ... sinon c'est celui par défaut
						else {
							var theme = 'defaut';
						}
