						// 2. on vérifie si le thème n'est pas bypassé
						// 2.1 pas de css
						if (css_annuler) {
							var theme = 'no-theme';
						}
						// 2.2 css imposé
						else if (css_select) {
							var theme = my_responsiveslides.css2theme(css_select);
						}
						//
						// 3. on applique le thème au slider et au conteneur général
						if (theme) {
							me.addClass("theme-" + theme);
							if (options.container) {
								jQuery(options.container).addClass("theme-" + theme);
							}
						}
					}

					// on merge les options
					settings = jQuery.extend(options,settings);

					// on lance le script et on ajoute la classe 'initialized'
					me.responsiveSlides(settings).addClass('initialized');
				});

				// chargement des css
				if (css_annuler === false) {
					// on prend la css forcée en priorité
					if (css_select !== false) {
						my_responsiveslides.addStylesheet(css_select);
					}
					// sinon on charge les feuilles de styles référencées dans les options
					else if (stylesheets.length) {
						for ( var index=0; index<stylesheets.length; index++ ) {
							my_responsiveslides.addStylesheet(stylesheets[index]);
						}
					}
					// sinon on prend la feuille de style par défaut
