	visibility: hidden;
}
#frm_pro_content_length_12,
#frm_pro_content_length_21 {
	visibility: visible;
}
#frm_pro_content_length_12::after {
	content: ' sur 2500 caractères max.';
}
#frm_pro_content_length_21::after {
	content: ' sur 1500 caractères max.';
}


.frm_forms .frm_error_style {
